import { useState } from "react";
import "./App.css";
import Axios from "axios";

function App() {
  const [advice, setAdvice] = useState("");

  const fetchData = () => {
    Axios.get("https://api.adviceslip.com/advice").then((res) => {
      setAdvice(res.data.slip.advice);
    });

    useEffect(() => {
      fetchData();
    }, []);
  };
  return (
    <div className="App">
      <h1>{advice}</h1>
    </div>
  );
}

export default App;
